import React, { Component } from 'react'
import Draggable from 'react-draggable'

class TextBox extends Component {
  state = {
    activeDrags: 0,
    conrolledPosition: {
      x: this.props.val.pos.x,
      y: this.props.val.pos.y
    },
    id: this.props.val.id,
    rew_data: this.props.val.data
  }
  componentDidMount() {
    console.log(this.state);
  }

  onStart = () => {
    this.setState({ activeDrags: ++this.state.activeDrags })
  }
  onStart = () => {
    this.setState({ activeDrags: ++this.state.activeDrags })
  }

  onControlledDrag = (e, position) => {
    const { x, y } = position
    this.setState({ conrolledPosition: { x, y } })
  }

  render() {
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop }
    const { controlledPosition } = this.state
    return (
      <Draggable style={{
        top: this.state.controlledPosition.x,
        left: this.state.controlledPosition.y
      }} position={controlledPosition} onDrag={this.onControlledDrag} {...dragHandlers}>
        <h1>Hello World</h1>
      </Draggable>
    )
  }
}

export default TextBox
