import React, { Component } from 'react'
import Draggable from 'react-draggable'

class TextBox extends Component {
  state = {
    activeDrags: 0,
    conrolledPosition: {
      x: this.props.val.pos.x,
      y: this.props.val.pos.y
    },
    id: this.props.val.id,
    rew_data: this.props.val.data
  }

  constructor(props) {
    super(props)
    var cx = this.props.val.pos.x
    var cy = this.props.val.pos.y
  }

  onStart = () => {
    this.setState({ activeDrags: ++this.state.activeDrags })
  }
  onStart = () => {
    this.setState({ activeDrags: ++this.state.activeDrags })
  }

  onControlledDrag = (e, position) => {
    const { x, y } = position
    this.setState({ conrolledPosition: { x, y } })
  }

  render() {
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop }
    const { controlledPosition } = this.state
    return (
      <Draggable position={controlledPosition} onDrag={this.onControlledDrag} {...dragHandlers}>
        <h1 style={{
          transform: `translate (${this.cx}px,${this.cy}px)`
        }}>Hello World</h1>
      </Draggable>
    )
  }
}

export default TextBox
